const addItem = localStorage.getItem('cartItems')
    ? JSON.parse(localStorage.getItem('cartItems'))
    : [];

const controlItems = (state = addItem, action) => {
    const product = action.payload;
    let product_before;
    if (product) {
        console.log(state);
        console.log(product.id);
        product_before = state.find(x => x.id === product.id);

    }
    let valueReturn = state;
    switch (action.type) {
        case 'ADDITEM':
            console.log('aca');
            console.log(product)
            console.log(product_before)
            if (product.qty == null || product_before.qty < product.stock) {
                //Check if Product is Already Exist
                const exist = state.find((x) => x.id === product.id);
                if (exist) {
                    //Increase Quantity
                    valueReturn = state.map((x) => x.id === product.id ? { ...x, qty: x.qty + 1 } : x);
                } else {
                    valueReturn = [
                        ...state,
                        {
                            ...product,
                            qty: 1,
                        }
                    ]
                }
            } else {
                window.alert('Lo siento. Stock insuficiente...');
            }
            localStorage.setItem('cartItems', JSON.stringify(valueReturn));
            return valueReturn;

        // return [...state, action.payload]
        case 'RESTITEM':
            const exist_del = state.find((x) => x.id === product.id);
            if (exist_del.qty === 1) {
                valueReturn = state.filter((x) => x.id !== exist_del.id);
            } else {
                valueReturn = state.map((x) => x.id === product.id ? { ...x, qty: x.qty - 1 } : x);
            }
            localStorage.setItem('cartItems', JSON.stringify(valueReturn));
            return valueReturn;

        case 'DELITEM':
            valueReturn = state = state.filter((x) => {
                return x.id !== action.payload.id
            })
            localStorage.setItem('cartItems', JSON.stringify(valueReturn));
            return valueReturn;

        default: return state;
    }
}

export default controlItems;
