import React, { useState } from 'react'
import { NavLink } from 'react-router-dom';
//import DATA from '../Data'
import moneyString from '../utils/Formats';
import ErrorMessage from './secondarys/MessageBox';
import LoadingBox from './secondarys/LoadingBox';
import { Products_sv } from '../utils/ConectorSV';
import { useDispatch } from 'react-redux';
import { addItem } from '../redux/actions';
let DATA;


const HeaderProducts = () => {
  return (
    <>
      <div className="container py-5">
        <div className="row">
          <div className="col-12 text-center">
            <h1>PRODUCTOS</h1>
            <hr />
          </div>
        </div>
      </div>
    </>
  )
};

const Products = () => {
  DATA = Products_sv();

  // console.log(DATA);
  const firstSate = { alias: 'addCart', text: 'Agregar al carrito' };
  const [cartBtn, setCartBtn] = useState(firstSate);


  const dispatch = useDispatch();

  const handleCart = (product) => {
    if (cartBtn.alias === 'addCart') {
      dispatch(addItem(product));
      setCartBtn({ alias: 'addMore', text: 'Agregar más' });
    } else {
      dispatch(addItem(product));
    }
  };



  const cardItem = (item) => {
    return (
      <div className="card my-5 py-4" key={item.id} style={{ width: '18rem' }}>
        <img src={item.img} className="card-img-top" alt={item.title} />
        <div className="card-body text-center">
          <h5 className="card-title">{item.title}</h5>
          <p className="lead">{moneyString(item.price)}</p>
          <NavLink to={`/products/${item.id}`} className="btn btn-outline-primary px-3 ali">Ver</NavLink>
          <button onClick={() => handleCart(item)} className="btn btn-outline-primary my-4">{cartBtn.text}</button>
        </div>
      </div>
    );
  };

  return (
    <>
      <div>
        <HeaderProducts />
        <div className="container">
          <div className="row justify-content-around">
            {DATA === true ? <LoadingBox /> : DATA.error === true ? <ErrorMessage variant="danger">{DATA.message}</ErrorMessage> : DATA.map(cardItem)}
          </div>
        </div>
      </div>
    </>
    //showProducts(DATA)
  )
}

export default Products
