import React, { useEffect, useState } from 'react'
import { useDispatch, useSelector } from 'react-redux';
import { useNavigate } from 'react-router-dom';
import { savePaymentMethod } from '../../redux/actions';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import Collapse from 'react-bootstrap/Collapse';
import Toasts from '../secondarys/toasts';

const CheckoutPayment = () => {
    const navigate = useNavigate();
    const dispatch = useDispatch();

    const state = useSelector((state) => state.controlItems);
    const {
        cart: { shippingAddress, paymentMethod },
    } = state;
    const [paymentMethodName, setPaymentMethod] = useState(paymentMethod || 'transferencia');
    const allData = ((shippingAddress.fullName != null && shippingAddress.fullName !== '') && (shippingAddress.rut != null && shippingAddress.rut !== '') && (shippingAddress.addressPhone != null && shippingAddress.addressPhone !== '') && (shippingAddress.address != null && shippingAddress.address !== '') && (shippingAddress.address2 != null && shippingAddress.address2 !== '') && (shippingAddress.city != null && shippingAddress.city !== '') && (shippingAddress.region != null && shippingAddress.region !== '') && (shippingAddress.postalCode != null && shippingAddress.postalCode !== '') && (shippingAddress.additional != null && shippingAddress.additional !== ''))

    let selected = false;

    const [nameBilling,setNameBilling] = useState('');

    useEffect(() => {
        if (!allData) {
            console.log('falta info');
            navigate('/checkout/shipping');
        } else {
            console.log('no falta info');
        }
    }, [allData, navigate]);

    const submitHandler = (e) => {
        e.preventDefault()
        if (selected) {
            // dispatch(savePaymentMethod(paymentMethodName));
            // localStorage.setItem('paymentMethod', paymentMethodName);
            // navigate('/checkout/placeorder');
        } else {
            Toasts('Por favor elige un método de pago');
        }

    }
    const ShippingVisitForm = () => {
        return (
            <div className='container mt-3'>
                <div className='py-1 text-center'>
                    <h5>Información de facturación</h5>
                </div>
                <div className='py-2'>
                    <form className="row g-3">
                        <div className="col-md-12">
                            <label htmlFor="inputEmail4" className="form-label">Nombre sociedad</label>
                            <input type="text" className="form-control" id="inputNameVisit" required onChange={(e) => true} />
                        </div>
                        <div className="col-md-12">
                            <label htmlFor="inputPassword4" className="form-label">Rut</label>
                            <input type="text" className="form-control" id="inputLastNameVisit" required onChange={(e) => true} />
                        </div>
                        <div className="col-md-12">
                            <label htmlFor="inputEmail4" className="form-label">Giro</label>
                            <input type="text" className="form-control" id="inputRutVisit" required onChange={(e) => true} />
                        </div>
                        <div className="col-12">
                            <label htmlFor="inputAddress" className="form-label">Dirección</label>
                            <input type="text" className="form-control" id="inputAddressVisit" placeholder="Av. Central 622" required onChange={(e) => true} />
                        </div>
                        <div className="col-12">
                            <label htmlFor="inputAddress2" className="form-label">Dirección 2</label>
                            <input type="text" className="form-control" id="inputAddress2Visit" placeholder="Departamento, oficina, piso." required onChange={(e) => true} />
                        </div>
                        <div className="col-md-6">
                            <label htmlFor="inputCity" className="form-label">Ciudad</label>
                            <input type="text" className="form-control" id="inputCityVisit" required onChange={(e) => true} />
                        </div>
                        <div className="col-md-4">
                            <label htmlFor="inputState" className="form-label">Región</label>
                            <select id="inputStateVisit" className="form-select" defaultValue=' ' required onChange={(e) => true}>
                                <option> </option>
                                <option>Antofagasta</option>
                                <option>Arica y Parinacota</option>
                                <option>Atacama</option>
                                <option>Aysén</option>
                                <option>Biobío</option>
                                <option>Coquimbo</option>
                                <option>La Araucanía</option>
                                <option>Los Lagos</option>
                                <option>Los Ríos</option>
                                <option>Magallanes</option>
                                <option>Maule</option>
                                <option>Metropolitana</option>
                                <option>Ñuble</option>
                                <option>O'Higgins</option>
                                <option>Tarapacá</option>
                                <option>Valparaíso</option>
                            </select>
                        </div>
                        <div className="col-md-2">
                            <label htmlFor="inputZip" className="form-label">N° Postal</label>
                            <input type="text" className="form-control" id="inputPostalVisit" onChange={(e) => true} />
                        </div>
                    </form>
                </div>
            </div>
        );
    }

    const UserComplete = () => {
        return (
            <div className="container border">
                <div className='col-md-7 col-lg-11 mx-auto' >
                    <h4 className='text-center mt-3 mb-2'>
                        Datos de envío
                    </h4>
                    <ul className="list-group mb-3">

                        <li className="list-group-item d-flex justify-content-between bg-light " >
                            <span className='me-2'>Nombre:</span>{shippingAddress.fullName}
                        </li>
                        <li className="list-group-item d-flex justify-content-between bg-light  " >
                            <span className='me-2'>Rut: </span> {shippingAddress.rut}
                        </li>
                        <li className="list-group-item d-flex justify-content-between bg-light  " >
                            <span className='me-2'>Teléfono: </span> {shippingAddress.addressPhone}
                        </li>
                        <li className="list-group-item d-flex justify-content-between bg-light " >
                            <span className='me-2'>Dirección: </span> {shippingAddress.address}, {shippingAddress.address2}, {shippingAddress.city}, {shippingAddress.region}
                        </li>
                        {shippingAddress.additional != null && shippingAddress.additional !== '' ? <li className="list-group-item d-flex justify-content-between bg-light " >
                            <span className='me-2'>Información: </span> {shippingAddress.additional}
                        </li> : <></>}
                    </ul>


                </div>
            </div>
        );
    };



    const PayMethod = () => {
        const [open, setOpen] = useState(false);

        const setSelect = () => {
            selected = true;
        }

        return (
            <div className='container border'>
                <h4 className='text-center py-3 my-1'>
                    Datos Facturación
                </h4>

                <div className="container ps-5 ms-4">

                    <Form.Check
                        id="CheckSameAddress"
                        label="Misma dirección de envío"
                        onChange={() => setOpen(!open)}
                        aria-controls="example-collapse-text"
                        aria-expanded={open}
                        defaultChecked
                    />
                </div>

                <Collapse in={open}>
                    <div id="example-collapse-text">
                        <ShippingVisitForm />
                    </div>
                </Collapse>

                <form action="" onSubmit={submitHandler} className='my-4'>
                    <div className="row justify-content-center">
                        <div className='col-lg-4 py-2'>
                            <h5 className='text-center'>
                                Método de pago
                            </h5>
                            <div className="form-check">
                                <input className="form-check-input" type="radio" name="flexRadioDefault" id="flexRadioDefault1" onChange={setSelect}/>
                                <label className="form-check-label" htmlFor="flexRadioDefault1">
                                    Transferencia
                                </label>
                            </div>
                            <div className="form-check">
                                <input className="form-check-input" type="radio" name="flexRadioDefault" id="flexRadioDefault2" onChange={setSelect}/>
                                <label className="form-check-label" htmlFor="flexRadioDefault2">
                                    WebPay
                                </label>
                            </div>
                            <div className="form-check">
                                <input className="form-check-input" type="radio" name="flexRadioDefault" id="flexRadioDefault3" onChange={setSelect}/>
                                <label className="form-check-label" htmlFor="flexRadioDefault3">
                                    Khipu
                                </label>
                            </div>
                            <div className="form-check">
                                <input className="form-check-input" type="radio" name="flexRadioDefault" id="flexRadioDefault4" onChange={setSelect}/>
                                <label className="form-check-label" htmlFor="flexRadioDefault4">
                                    Paypal
                                </label>
                            </div>
                            <div className="form-check">
                                <input className="form-check-input" type="radio" name="flexRadioDefault" id="flexRadioDefault5" onChange={setSelect}/>
                                <label className="form-check-label" htmlFor="flexRadioDefault5">
                                    Criptomonedas
                                </label>
                            </div>
                        </div>
                        <div className="col-lg-5 d-grid gap-2 py-2">
                            <button className='btn btn-outline-primary btn-lg fw-bold' type='submit' style={{ fontSize: '25px' }}>PROCESAR ORDEN</button>
                        </div>
                    </div>
                </form>
            </div>
        );
    }





    return (
        <div>
            <UserComplete />
            <PayMethod />
        </div>
    )
}

export default CheckoutPayment
