import React from 'react'
import { toast } from 'react-toastify';

const ToastContainer = document.getElementById('main-toasts');




const Toasts = (text, prop) => {
    toast.error(text, {
        position: prop.positon || "top-right",
        autoClose: prop.autoClose || 5000,
        hideProgressBar: prop.hideProgressBar || false,
        closeOnClick: prop.closeOnClick || true,
        pauseOnHover: prop.pauseOnHover || false,
        draggable: prop.draggable || true,
        progress: prop.progress || undefined,
        theme: prop.theme || "light",
    });
}

export default Toasts
