import React, { useReducer } from 'react'
import LoadingBox from './LoadingBox';
import { useNavigate } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import axios from 'axios';
import { clearCart } from '../../redux/actions';
import { getError } from '../../utils/utils';
import Toasts from './toasts';
import moneyString from '../../utils/Formats';

const reducer = (state, action) => {
    switch (action.type) {
        case 'CREATE_REQUEST':
            return { ...state, loading: true };
        case 'CREATE_SUCCES':
            return { ...state, loading: false };
        case 'CREATE_FAIL':
            return { ...state, loading: false };
        default:
            return state;
    }
}

const OrderSummary = () => {
    const state = useSelector((state) => state.controlItems);
    const {
        cart, userInfo,
    } = state;

    
    const navigate = useNavigate();
    const ctxDispatch = useDispatch();
    const [{ loading }, dispatch] = useReducer(reducer, {
        loading: false,
    });

    cart.itemsPrice = cart.cartItems.reduce((a, c) => a + c.qty * c.price, 0);
    cart.shippingPrice = cart.itemsPrice >= process.env.MIN_FREE_SHIPPING ? 0 : -1;
    cart.taxes = cart.itemsPrice * 0.19;
    cart.totalPrice = cart.shippingPrice !== -1 ? cart.itemsPrice + cart.shippingPrice : cart.itemsPrice;

    const procedeToPay = async (value) => {
        try {
            dispatch({ type: 'CREATE_REQUEST' });
            const { data } = await axios.post(
                '/api/orders/new-order',
                {
                    productList: cart.cartItems, //ADMIN, CLIENT, DEV
                    shippingAddress: cart.shippingAddress,
                    billingAddress: cart.billingAddress,
                    paymentMethod: cart.paymentMethod,
                    itemsPrice: cart.itemsPrice,
                    shippingPrice: cart.shippingPrice,
                    taxPrice: cart.taxes,
                    totalPrice: cart.totalPrice,
                    _id: userInfo._id,
                },
                {
                    headers: {
                        authorization: String(userInfo.token),
                    }
                });

            dispatch({ type: 'CREATE_SUCCESS' });
            ctxDispatch(clearCart());

            localStorage.removeItem('shippingAddress');
            localStorage.removeItem('billingAddress');
            localStorage.removeItem('cartItems');
            localStorage.removeItem('paymentMethod');
            //crear orden
            switch (value) {
                case 'transferencia':
                    navigate('/checkout/payorder/transf');
                    break;
                case 'webpay':
                    navigate('/checkout/payorder/webpay');
                    break;
                case 'khipu':
                    navigate('/checkout/payorder/khipu');
                    break;
                case 'paypal':
                    navigate('/checkout/payorder/paypal');
                    break;
                default:
                    navigate('/checkout/placeorder');
                    break;
            }
        } catch (error) {
            dispatch({ type: 'CREATE_FAIL' });
            Toasts(getError(error));
            console.log(error.stack);
        }

    };
    return (
        <div className='container'>
            <div className="container border" style={{fontSize:'18px'}}>
                <div className='pt-3'>
                    <h4>Resumen de Orden</h4>
                </div>
                <div className="row justify-content-between mx-auto py-1">
                    <div className='col-4'>Productos:</div> <div className='col-4'>{moneyString(cart.itemsPrice)}</div>
                    <hr />
                </div>
                <div className="row justify-content-between mx-auto py-1">
                    <div className='col-4'>Envío:</div> <div className='col-4'>{cart.shippingPrice ===-1 ? 'Por pagar':cart.shippingPrice}</div>
                    <hr />
                </div>
                <div className="row justify-content-between mx-auto py-1" style={{fontSize:'15px'}}>
                    <div className='col-4'>Impuestos:</div> <div className='col-4'>{moneyString(cart.taxes)}</div>
                    <hr />
                </div>
                <div className="row justify-content-between mx-auto pb-4 ">
                    <div className='col-4 fw-bold'>TOTAL:</div> <div className='col-7'><strong>{moneyString(cart.itemsPrice)}</strong> <span className='ms-2' style={{fontSize:'12px'}}>IVA incluido</span> </div>
                </div>
            </div>
            <div className="">
                <button className="btn btn-outline-primary btn-lg fw-bold w-100" onClick={() => { procedeToPay(cart.paymentMethod) }} style={{ fontSize: '25px' }}>PAGAR</button>
            </div>
            {loading && <LoadingBox />}
        </div>

    )
}

export default OrderSummary;
