import React from 'react'
import { useSelector } from 'react-redux'
import { useDispatch } from 'react-redux'
import { addItem, restItem } from '../redux/actions/index'


const Cart = () => {
    const state = useSelector((state) => state.controlItems);
    const dispatch = useDispatch();

    const cartItem = (cartItem) => {
        return(
            <div className="px-4 my-5 bg-light rounded-3" key={cartItem.id}>
                <div className="containter py-4">
                    <button className="btn-close float-end " aria-label='Close'></button>
                    <div className="row justify-content-center">
                         <div className="col-md-4">
                            <img src={cartItem.img} alt={cartItem.title} />
                         </div>
                        <div className="col-md-4">
                        <h3></h3>
                        <p></p>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
    return (
        <>
            {state.length !== 0 && state.map(cartItem)}
        </>
    )
}

export default Cart
