import React, { useEffect, useReducer } from 'react'
import LoadingBox from '../secondarys/LoadingBox.js'
import { MessageBox } from '../secondarys/Messages.js'
import { useSelector } from 'react-redux';
import { useNavigate, useParams } from 'react-router-dom';
import { getError } from '../../utils/utils.js';
import axios from 'axios';
import OrderSummary from '../secondarys/OrderSummary.jsx';
import moneyString from '../../utils/Formats.js';

function reducer(state, action) {
    switch (action.type) {
        case 'FETCH_REQUEST':
            return { ...state, loading: true, error: '' };
        case 'FETCH_SUCCESS':
            return { ...state, loading: false, order: action.payload, error: '' };
        case 'FETCH_FAIL':
            return { ...state, loading: false, error: action.payload };
        default:
            return state;
    }
}

const OrderScreen = () => {
    const state = useSelector((state) => state.controlItems);
    const { userInfo } = state;

    const params = useParams();
    const { id: orderId } = params
    const navigate = useNavigate();

    const [{ loading, error, order }, dispatch] = useReducer(reducer, {
        loading: true,
        order: {},
        error: '',
    });

    useEffect(() => {
        const fetchOrder = async () => {
            try {
                dispatch({ type: 'FETCH_REQUEST' });
                const { data } = await axios.get(`/api/orders/${orderId}`, {
                    headers: { authorization: userInfo.token },
                });
                dispatch({ type: 'FETCH_SUCCESS', payload: data });
            } catch (error) {
                dispatch({ type: 'FETCH_FAIL', payload: getError(error) });
            }
        };
        if (userInfo == null) {
            return navigate('/checkout/signin');
        }
        if (order._id == null || order._id !== orderId) {
            fetchOrder();
        }
    }, [order, userInfo, orderId, navigate]);



    const ProductList = (cartItem) => {
        return (
            <div className="px-4 my-2 bg-light rounded-3" key={cartItem.id}>
                <div className="containter py-4">
                    <div className="row justify-content-between">
                        <div className="col-3">
                            <img src={cartItem.img} alt={cartItem.name} height={70}  />
                        </div>
                        <div className="col-7 justify-content-center">
                            <div className="row justify-content-between">
                            <h5 className='col-3 py-1'>{cartItem.name}</h5>
                            <p className='col-2 lead' >{`Precio: ${moneyString(cartItem.price)}\n`}
                                {`Cantidad: ${cartItem.qty}`}
                            </p>
                            <p className='col-2 lead fw-bold py-3'>{`Total: ${moneyString(cartItem.price * cartItem.qty)}`}</p>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }


    return (
        loading
            ? <LoadingBox />
            : error
                ? <MessageBox variant="danger">{error}</MessageBox>
                :
                <div className='container py-3'>
                    <div className="row">
                        <div className="col-4 py-2 order-md-last mx-auto"><OrderSummary /></div>
                        <div className="col-8"><h1>Orden {orderId}</h1>
                            <div className="card card-null mb-2" >
                                <div className="card-body" id='shipping-values' >
                                    <h5 className="card-title">Envío</h5>
                                    <div className="card-text mb-3">
                                        <strong className='me-1'>Nombre:</strong>{order.shippingAddress.fullName} 
                                        <strong className='mx-1 ps-3'>Teléfono:</strong>{order.shippingAddress.phoneAddress} <br />
                                        <strong className='me-1'>Dirección:</strong>{order.shippingAddress.address}, {order.shippingAddress.city}, {order.shippingAddress.region} {order.shippingAddress.postalCode != null && order.shippingAddress.postalCode !== '' ? `, ${order.shippingAddress.postalCode}` : ''}
                                    </div>
                                    {order.isDelivered ? (
                                        <MessageBox variant='success' aling='start'>Entregado. Fecha de entrega : {order.deliveredAt}</MessageBox>
                                    ) : (
                                        <MessageBox variant='danger' aling='start'>No entregado</MessageBox>
                                    )}
                                </div>
                            </div>
                            <div className="card card-null " >
                                <div className="card-body" id='shipping-values' >
                                    <h5 className="card-title">Pago</h5>
                                    <div className="card-text  mb-3">
                                        <strong>Método:</strong> {order.paymentMethod} <br />
                                    </div>
                                    {order.isPaid ? (
                                        <MessageBox variant='success' aling='start'>Pagado. Fecha de pago : {order.paidAt}</MessageBox>
                                    ) : (
                                        <MessageBox variant='danger' aling='start'>No pagado</MessageBox>
                                    )}
                                </div>
                            </div>
                            <div>
                                {order.orderItems.map(ProductList)}
                            </div>
                        </div>
                    </div>
                </div>

    )
}

export default OrderScreen
