import React, { useState } from 'react'
import { NavLink } from 'react-router-dom';
import Products_sv from '../utils/ConectorSV';
//import DATA from '../Data'
import moneyString from '../utils/Formats';

const cardItem = (item) => {
  return (
    <div className="card my-5 py-4" key={item.id} style={{ width: '18rem' }}>
      <img src={item.img} className="card-img-top" alt={item.title} />
      <div className="card-body text-center">
        <h5 className="card-title">{item.title}</h5>
        <p className="lead">{moneyString(item.price)}</p>
        <NavLink to={`/products/${item.id}`} className="btn btn-outline-primary px-3 ali">Ver</NavLink>
        <a href="#" className="btn btn-primary ms-3">Agregar al carrito</a>
      </div>
    </div>
  );
};
const HeaderProducts = () => {
  return (
    <>
      <div className="container py-5">
        <div className="row">
          <div className="col-12 text-center">
            <h1>PRODUCTOS</h1>
            <hr />
          </div>
        </div>
      </div>
    </>
  )
};

const Products = () => {
  const DATA = Products_sv();
  console.log(DATA);
  
  const LoadingProducts = () =>{
    return(
      <>
      <div>Loading...</div> 
      </>
    );
  }
  const ErrorMessage = () =>{
    return(
      <>
      <div>{DATA.message}</div>
      </>
    );
  }
  const loadingProducts = () =>{
    return(
      <>
      </>
    );
  }
  
  
  
  
  const [showProducts, setData] = useState([]);
  
  const toShow = DATA ? <LoadingProducts/>: DATA.error ? <ErrorMessage/> : DATA.map(cardItem);
  setData(toShow);
  console.log(showProducts);
  return (
    <div>
      <HeaderProducts />
      <div className="container">
        <div className="row justify-content-around">
          {showProducts}
        </div>
      </div>
    </div>
  )
}

export default Products
