import React, { useContext, useState } from 'react'
import axios from 'axios';
import store from '../../redux/store';
import { useDispatch } from 'react-redux';
import { signin } from '../../redux/actions';

const MyProfile = () => {

    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');

    const state = useContext((s) => s.controlItems);
    const dispatch = useDispatch();

    const submitHandler = async (e) => {
        e.preventDefault();
        try {
            const { data } = await axios.post('/api/users/signin', {
                email,
                password,
            });
            dispatch(signin(data));
            localStorage.setItem('userInfo', JSON.stringify(data));
        } catch (error) {
            console.log(error.stack);
            alert('Invalid email or password');
        }
    };
    return (
        <>
            <div class="dropdown">
                <button className="btn btn-outline-primary ms-auto dropdown-toggle" type="button" data-bs-toggle="dropdown" aria-expanded="false">
                <span className='fa fa-user me-1'></span>My perfil
                </button>
                <ul class="dropdown-menu">
                    <li><a class="dropdown-item" href="#">Action</a></li>
                    <li><a class="dropdown-item" href="#">Another action</a></li>
                    <li><a class="dropdown-item" href="#">Something else here</a></li>
                </ul>
            </div>
        </>
    )
}

export default MyProfile
