import React, { useEffect } from 'react'
import { useSelector } from 'react-redux';
import { useNavigate } from 'react-router-dom';
import CheckoutBar from './checkout/CheckoutBar.jsx';

const Checkout = (props) => {
    const navigate = useNavigate()

    const state = useSelector((state) => state.controlItems);

    useEffect(() => {
        console.log('a');
        if (state == null || state.cart == null || Object.entries(state.cart).length === 0 || state.cart.cartItems == null || state.cart.cartItems.length === 0) {
            navigate('/cart');
        } else if ((state.userInfo == null || state.userInfo === '') && props.state !== 1) {
            navigate('/checkout/signin');
        } else if (props.state === 1 && state.userInfo != null && state.userInfo !== '') {
            navigate('/checkout/shipping');
        }
    });
    
    return (
        <>
            <div className="container my-5">
                <div className="row g-5 mx-auto">
                    <div className="col-md-5 col-lg-5 order-md-last mx-auto">
                        {props.cart}
                    </div>
                    <div className="col-md-7 col-lg-7 mx-auto">
                        <div className="row">
                            <><CheckoutBar state={props.state} /></>
                        </div>
                        <div className="container py-3 mx-auto">
                            {props.child}
                        </div>
                    </div>
                </div>
            </div>

        </>
    )
}

export default Checkout
