import React from 'react'
import { toast } from 'react-toastify';

const ToastContainer = document.getElementById('main-toasts');




const Toasts = (text, prop) => {
    const position = prop.positon ? prop.positon : "top-right";
    const autoClose = prop.autoClose ? prop.autoClose : 5000;
    const hideProgressBar = prop.hideProgressBar ? prop.hideProgressBar : false;
    const closeOnClick = prop.closeOnClick ? prop.closeOnClick : true;
    const pauseOnHover = prop.pauseOnHover ? prop.pauseOnHover : false;
    const draggable = prop.draggable ? prop.draggable : true;
    const progress = prop.progress ? prop.progress : undefined;
    const theme = prop.theme ? prop.theme : "dark";


        toast.error(text, {
            position: position,
            autoClose: autoClose,
            hideProgressBar: hideProgressBar,
            closeOnClick: closeOnClick,
            pauseOnHover: pauseOnHover,
            draggable: draggable,
            progress: progress,
            theme: theme,
        });
}

export default Toasts
