export const MyToast = (props) => {
    return (
        <div className="toast-container position-fixed bottom-0 end-0 p-3">
            <div id="liveToast" className="toast toast-invalid-user" role="alert" aria-live="assertive" aria-atomic="true">
                <div className="toast-header text-bg-danger ">
                    {/* <img src="..." className="rounded me-2" alt="..."> */}
                    <strong className="me-auto">{props.tittle}</strong>
                    <small>auto hide</small>
                    <button type="button" className="btn-close" data-bs-dismiss="toast" aria-label="Close"></button>
                </div>
                <div className="toast-body">
                    {props.children}
                </div>
            </div>
        </div>
    );
}
export const ToastPassowrd = (props) => {
    return (
        <div className="toast-container position-fixed bottom-0 end-0 p-3">
            <div id="liveToast" className="toast toast-pass-match-false" role="alert" aria-live="assertive" aria-atomic="true">
                <div className="toast-header text-bg-danger ">
                    {/* <img src="..." className="rounded me-2" alt="..."> */}
                    <strong className="me-auto">{props.tittle}</strong>
                    <small>auto hide</small>
                    <button type="button" className="btn-close" data-bs-dismiss="toast" aria-label="Close"></button>
                </div>
                <div className="toast-body">
                    {props.children}
                </div>
            </div>
        </div>
    );
}
export const ToastErrorAddAddress = () => {
    return (
        <div className="toast-container position-fixed bottom-0 end-0 p-3">
            <div id="liveToast" className="toast toast-error-add-address" role="alert" aria-live="assertive" aria-atomic="true">
                <div className="toast-header text-bg-danger ">
                    {/* <img src="..." className="rounded me-2" alt="..."> */}
                    <strong className="me-auto">¡ERROR!</strong>
                    <small>auto hide</small>
                    <button type="button" className="btn-close" data-bs-dismiss="toast" aria-label="Close"></button>
                </div>
                <div className="toast-body">
                    Error agregando nueva dirección
                </div>
            </div>
        </div>
    );
}
export const ToastErrorFillFields = () => {
    return (
        <div className="toast-container position-fixed bottom-0 end-0 p-3">
            <div id="liveToast" className="toast toast-fill-all-fields" role="alert" aria-live="assertive" aria-atomic="true">
                <div className="toast-header text-bg-danger ">
                    {/* <img src="..." className="rounded me-2" alt="..."> */}
                    <strong className="me-auto">CAMPOS VACÍOS</strong>
                    <small>auto hide</small>
                    <button type="button" className="btn-close" data-bs-dismiss="toast" aria-label="Close"></button>
                </div>
                <div className="toast-body">
                    Por favor rellene todos los campos
                </div>
            </div>
        </div>
    );
}
