import React from 'react'
import { toast } from 'react-toastify';

const ToastContainer = document.getElementById('main-toasts');




const Toasts = (text, prop) => {
    toast.error(text, {
        position: "bottom-center",
        autoClose: 5000,
        hideProgressBar: false,
        closeOnClick: true,
        pauseOnHover: true,
        draggable: true,
        progress: 0.1,
        theme: "light",
    });
}

export default Toasts
