import React from 'react'
import { toast } from 'react-toastify';

const ToastContainer = document.getElementById('main-toasts');




const Toasts = (text, prop) => {
    toast.error(text, {
        position: prop.positon ? prop.positon : "top-right",
        autoClose: prop.autoClose ? prop.autoClose : 5000,
        hideProgressBar: prop.hideProgressBar ? prop.hideProgressBar : false,
        closeOnClick: prop.closeOnClick ? prop.closeOnClick : true,
        pauseOnHover: prop.pauseOnHover ? prop.pauseOnHover : false,
        draggable: prop.draggable ? prop.draggable : true,
        progress: prop.progress ? prop.progress : undefined,
        theme: prop.theme ? prop.theme : "dark",
    });
}

export default Toasts
