import React from 'react'
import { useSelector } from 'react-redux'
import { useDispatch } from 'react-redux'
import { addItem, restItem, deleteItem } from '../redux/actions/index'
import { NavLink } from 'react-router-dom'
import moneyString from '../utils/Formats'


const Cart = () => {
    const state = useSelector((state) => state.controlItems);
    const dispatch = useDispatch();

    const delItem = (item) => {
        dispatch(deleteItem(item))
    }


    const handleButton = (product,ADD) => {
      if (cartBtn.alias === 'addCart') {
        dispatch(addItem(product));
        setCartBtn({ alias: 'addMore', text: 'Agregar más' });
      } else {
        dispatch(restItem(product));
        setCartBtn(firstSate);
      }
    };





    const cartItem = (cartItem) => {
        console.log(cartItem);
        return (
            <div className="px-4 my-5 bg-light rounded-3" key={cartItem.id}>
                <div className="containter py-4">
                    <div className="">
                        <h3 className='text-center'>{cartItem.title}</h3>
                        <hr />
                    </div>
                    <button onClick={() => delItem(cartItem)} className="btn-close float-end" aria-label='Close'></button>
                    <div className="row justify-content-center">
                        <div className="col-md-3 text-center">
                            <img src={cartItem.img} alt={cartItem.title} height={200} width={180} />
                        </div>
                        <div className="col-md-2 ">
                            <p className='lead'>{`Precio: ${moneyString(cartItem.price)}`}</p>
                            <p className='lead'>{`Cantidad: ${cartItem.qty}`}</p>
                            <button className="btn btn-outline-dark me-4" onClick={() => handleButton(cartItem,true)}>
                                <i className="fa fa-minus"></i>
                            </button>
                            <button className="btn btn-outline-dark" onClick={() => handleButton(cartItem,true)}>
                                <i className="fa fa-plus"></i>
                            </button>
                            <p className='lead fw-bold'>{`Total: ${moneyString(cartItem.price * cartItem.qty)}`}</p>
                        </div>

                        <div className="col-md-5" align='justify'>
                            <p className='lead'>{cartItem.shortDesc}</p>
                        </div>
                    </div>
                </div>
            </div>
        );
    }

    const emptyCart = () => {
        return (
            <div className="px-4 my-5 bg-light rounded-3">
                <div className="containter py-4">
                    <div className="row">
                        <h3>Tu Carrito está vacío</h3>
                    </div>
                </div>
            </div>
        );

    }

    const button = () => {
        return (
            <div className="container">
                <div className="row">
                    <NavLink to="/checkout" className="btn btn-outline-primary mb-5 w-25 mx-auto">Ir a Pagar</NavLink>
                </div>
            </div>
        );
    };

    return (
        <>
            {state.length === 0 && emptyCart()}
            {state.length !== 0 && state.map(cartItem)}
            {state.length !== 0 && button()}
        </>
    )
}

export default Cart
