import React from 'react'
import { toast } from 'react-toastify';

const ToastContainer = document.getElementById('main-toasts');




const Toasts = (text, prop) => {
    toast.error(text, {
        position: "top-right",
        autoClose: 5000,
        hideProgressBar: false,
        closeOnClick: true,
        pauseOnHover: false,
        draggable: true,
        progress: undefined,
        theme: "colored",
    });
}

export default Toasts
