import { toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css'

const Toasts = (text) => {
    toast.error(text, {
        position: "top-right",
        autoClose: 5000,
        hideProgressBar: false,
        closeOnClick: true,
        pauseOnHover: false,
        draggable: true,
        progress: undefined,
        theme: "light",
        });
}

export default Toasts
