import { ErrorMessage } from "../../components/secondarys/MessageBox";

const initialState = localStorage.getItem('cartItems')
    ? JSON.parse(localStorage.getItem('cartItems'))
    : { userInfo: null, cart: { cartItems: [] } };

const controlItems = (state = initialState, action) => {
    const product_in = action.payload;
    let product_before;
    if (product_in) {
        product_before = state.cart.cartItems.find(x => x.id === product_in.id);
    }
    let actualCart = state.cart;
    switch (action.type) {
        case 'ADDITEM':
            try {
                if (product_before == null || product_before.qty == null || product_before.qty < product_in.stock) {
                    //Check if Product is Already Exist
                    const exist = state.cart.cartItems.find((x) => x.id === product_in.id);
                    if (exist) {
                        //Increase Quantity
                        actualCart = state.cart.cartItems.map((x) => x.id === product_in.id ? { ...x, qty: x.qty + 1 } : x);
                    } else {
                        actualCart = [
                            ...state.cart,
                            {
                                ...product_in,
                                qty: 1,
                            }
                        ]
                    }
                } else {
                    window.alert('Lo siento. Stock insuficiente...');
                }
                localStorage.setItem('cartItems', JSON.stringify(valueReturn));
                return valueReturn;
            } catch (error) {
                console.log(error.stack);
                return (ErrorMessage(error));
            }


        // return [...state, action.payload]
        case 'RESTITEM':
            const exist_del = state.cart.find((x) => x.id === product_in.id);
            if (exist_del.qty === 1) {
                actualCart = state.cart.filter((x) => x.id !== exist_del.id);
            } else {
                actualCart = state.cart.map((x) => x.id === product_in.id ? { ...x, qty: x.qty - 1 } : x);
            }
            valueReturn = {}
            localStorage.setItem('cartItems', JSON.stringify(valueReturn));
            return valueReturn;

        case 'DELITEM':
            actualCart = state.cart = state.cart.filter((x) => {
                return x.id !== action.payload.id
            })
            localStorage.setItem('cartItems', JSON.stringify(valueReturn));
            return valueReturn;

        case 'USER_SIGNIN':
            return { ...state, userInfo: action.payload };

        default: return state;
    }
}

export default controlItems;
