let props = { tittle: '', children: '' };
export const setMyToast = (props_in) => {
    props = props_in;
}

export const MyToast = () => {
    return (
        <div className="toast-container position-fixed bottom-0 end-0 p-3">
            <div id="liveToast" className="toast toast-main" role="alert" aria-live="assertive" aria-atomic="true">
                <div className="toast-header text-bg-danger">
                    {/* <img src="..." className="rounded me-2" alt="..."> */}
                    <strong className="me-auto">{props.tittle}</strong>
                    <small>auto hide</small>
                    <button type="button" className="btn-close" data-bs-dismiss="toast" aria-label="Close"></button>
                </div>
                <div className="toast-body">
                    {props.children}
                </div>
            </div>
        </div>
    );
}
