import React from 'react'
import { toast } from 'react-toastify';

const ToastContainer = document.getElementById('main-toasts');




const Toasts = () => {
    toast.error('ðŸ¦„ Wow so easy!', {
        position: "bottom-left",
        autoClose: 5000,
        hideProgressBar: false,
        closeOnClick: true,
        pauseOnHover: true,
        draggable: true,
        progress: undefined,
        theme: "light",
    });
}

export default Toasts
