import './App.css';
import "../node_modules/bootstrap/dist/css/bootstrap.min.css";
import "../node_modules/bootstrap/dist/js/bootstrap.bundle.min.js";
import { Route, Routes } from 'react-router-dom'
import Header from './components/Header';
import Footer from './components/Footer';
import Home from './components/Home';
import About from './components/About';
import Contact from './components/Contact';
import Products from './components/Products';
import Product from './components/Product';
import Cart from './components/Cart';
import Checkout from './components/Checkout';
import AdminPanel from './components/AdminPanel';
import { MyToast, ToastPassowrd } from './components/secondarys/Toasts';
import MiniCart from './components/secondarys/MiniCart';
import Login from './components/buttons/Login';





function App() {
  return (
    <>
      <Header />
      <MyToast tittle="ERROR DE CONEXIÓN">Usuario o contraseña incorrectos</MyToast>
      <ToastPassowrd tittle="ERROR DE COINCIDENCIA">Las contraseñas no coinciden</ToastPassowrd>

      <Routes>
        <Route exact path='/' element={< Home />} />
        <Route exact path='/products/:id' element={<Product />} />
        <Route exact path='/products' element={<Products />} />
        <Route exact path='/cart' element={<Cart />} />
        <Route exact path='/checkout' element={<Checkout cart={<MiniCart/>} child={<><Login/> <Signup/></> }/>}/>
        <Route exact path='/about' element={<About />} />
        <Route exact path='/contact' element={<Contact />} />
        <Route exact path='/_controlpanel_' element={<AdminPanel />} />
      </Routes>

      <Footer />
    </>
  );
}

export default App;
