const addItem = [];

const controlItems = (state = addItem, action) => {
    const product = action.payload;
    let valueReturn = state;
    switch (action.type) {
        case 'ADDITEM':
            if (product.qty == null || product.qty < product.stock) {
                //Check if Product is Already Exist
                const exist = state.find((x) => x.id === product.id);
                if (exist) {
                    //Increase Quantity
                    valueReturn = state.map((x) => x.id === product.id ? { ...x, qty: x.qty + 1 } : x);
                } else {
                    valueReturn = [
                        ...state,
                        {
                            ...product,
                            qty: 1,
                        }
                    ]
                }
            }

            return valueReturn;

        // return [...state, action.payload]
        case 'RESTITEM':
            const exist_del = state.find((x) => x.id === product.id);
            if (exist_del.qty === 1) {
                return state.filter((x) => x.id !== exist_del.id);
            } else {
                return state.map((x) => x.id === product.id ? { ...x, qty: x.qty - 1 } : x);
            }
        case 'DELITEM':
            return state = state.filter((x) => {
                return x.id !== action.payload.id
            })
        default: return state;
    }
}

export default controlItems;
