        setPostalCode = (text) => { postalCode = text }
        setAdditional = (text) => { additional = text }

        setAlias = () => { alias = 'PRINCIPAL' };
        setAlias();

        const selectPhone = () => {
            const mySwitch = document.getElementById('switch-same-phone');
