/**
 * Retorna numero formateado como String
 * @param {Number} number
 * @returns String de numero con formato de dinero
 */
const moneyString = (number) => {
    number = String(number)
    const split = number.split('.');
    console.log(split);
    const decimal = split[1];
    number = split[0] ? split[0] : number;
    return ('$' + (String(number).replace(/(.)(?=(\d{3})+$)/g, '$1.')) + (decimal ? `,${decimal}` : ''));
};


export default moneyString;
