import React, { useEffect, useReducer } from 'react'
import { useSelector } from 'react-redux';
import { useNavigate } from 'react-router-dom';
import Toasts from '../secondarys/toasts';
import { getError } from '../../utils/utils';
import { Axios } from 'axios';

const reducer = (state, action) => {
    switch (action.type) {
        case 'CREATE_REQUEST':
            return { ...state, loading: true };
        case 'CREATE_SUCCES':
            return { ...state, loading: false };
        case 'CREATE_FAIL':
            return { ...state, loading: false };

        default:
            return state;
    }
}


const CheckoutPlaceorder = () => {

    const [{ loading, error }, dispatch] = useReducer(reducer, {
        loading: false,
        error: '',
    });

    const state = useSelector((state) => state.controlItems);
    const {
        cart,
    } = state;

    cart.itemsPrice = cart.cartItems.reduce((a, c) => a + c.qty * c.price, 0);
    cart.shippingPrice = cart.itemsPrice >= process.env.MIN_FREE_SHIPPING ? 0 : -1;
    cart.taxes = cart.itemsPrice * 0.19;
    cart.totalPrice = cart.shippingPrice !== -1 ? cart.itemsPrice + cart.shippingPrice : cart.itemsPrice;

    const ProcedeToPay = async (value) => {
        const navigate = useNavigate();
        try {
            dispatch({ type: 'CREATE_REQUEST' });
            const { data } = await Axios.post(
                '/api/createOrder',
                {
                    productList: cart.cartItems, //ADMIN, CLIENT, DEV
                    shippingAddress: cart.shippingAddress,
                    billingAddress: cart.billingAddress,
                    paymentMethod: cart.paymentMethod,
                    itemsAmount: { type: Number, required: true },
                    shippingAmount: { type: Number, required: true },
                    shippingMethod: { type: String },
                },
                {});
        } catch (error) {
            dispatch({ type: 'CREATE_FAIL' });
            Toasts(getError(error));
        }
        useEffect(() => {
            //crear orden
            switch (value) {
                case 'transferencia':
                    navigate('/checkout/payorder/transf');
                    break;
                case 'webpay':
                    navigate('/checkout/payorder/webpay');
                    break;
                case 'khipu':
                    navigate('/checkout/payorder/khipu');
                    break;
                case 'paypal':
                    navigate('/checkout/payorder/paypal');
                    break;
                default:
                    navigate('/checkout/placeorder');
                    break;
            }
        }, [value, navigate]);
    };

    const SendData = () => {
        return (
            <>
                <div className="col md-8">
                    <div className=" ">
                        <h5 className=" ">Envío </h5>
                        <p className=''>
                            <strong className='mx-2'>Nombre:</strong>{cart.shippingAddress.fullName} <br />
                            <strong className='mx-2'>Rut:</strong>{cart.shippingAddress.rut}
                            <strong className='ms-4 mx-2'>Tel.:</strong>{cart.shippingAddress.addressPhone} <br />
                            <strong className='mx-2'>Nombre:</strong>{cart.shippingAddress.fullName} <br />
                        </p>
                    </div>
                </div>
            </>
        );
    };

    const BillingData = () => {
        return (
            <>
                <p className=''>
                    <strong className='mx-2'>Tipo:</strong>{'Factura'} <br />
                    <strong className='mx-2'>Nombre:</strong>{cart.billingAddress.name} <br />
                    <strong className='mx-2'>Rut:</strong>{cart.billingAddress.rut}
                    <strong className='ms-4 mx-2'>Giro:</strong>{cart.billingAddress.giro} <br />
                    <strong className='mx-2'>Dirección:</strong>{cart.billingAddress.address}, {cart.billingAddress.city}, {cart.billingAddress.region}, {cart.billingAddress.postal}<br />
                </p>

            </>
        );
    };
    const PaymentData = () => {
        return (
            <>
                <div className="row">
                    <div className="col-5 my-1">
                        <h5 className="mt-3">Método de Pago </h5>
                        <p className='mb-4'>
                            <strong className='mx-2'>{cart.paymentMethod}</strong>
                        </p>
                    </div>
                    <div className="col-lg-6 d-grid py-2">
                        <button className="btn btn-outline-primary btn-lg fw-bold " onClick={() => { ProcedeToPay(cart.paymentMethod) }} style={{ fontSize: '25px' }}>PAGAR</button>
                    </div>
                </div>
            </>
        );
    }
    return (
        <div className='col-9 mx-auto'>
            <h1 className='mb-3 text-center'>Revisa tu orden</h1>
            <div className="row">
                <SendData />
            </div>
            <div className="row">
                <div className="col md-8">
                    <div className="">
                        <div className=" ">
                            <h5 className=" ">Facturación </h5>
                            {cart.billingAddress.type === 'invoice' ? <BillingData /> : <p className='card-text'><strong className='mx-2'>Boleta</strong></p>}
                        </div>
                    </div>
                </div>
            </div>
            <div className="row">
                <PaymentData />
            </div>
        </div>
    )
}

export default CheckoutPlaceorder
