import React from 'react'
import { useSelector } from 'react-redux'
import { useDispatch } from 'react-redux'
import { addItem, restItem } from '../redux/actions/index'


const Cart = () => {
    const state = useSelector((state) => state.controlItems);
    const dispatch = useDispatch();

    const deleteItem = (item) =>{
        dispatch()
    }


    const cartItem = (cartItem) => {
        return(
            <div className="px-4 my-5 bg-light rounded-3" key={cartItem.id}>
                <div className="containter py-4">
                    <button onClick={()=>deleteItem(cartItem)} className="btn-close float-end" aria-label='Close'></button>
                    <div className="row justify-content-center">
                         <div className="col-md-4">
                            <img src={cartItem.img} alt={cartItem.title} height={200} width={180}/>
                         </div>
                        <div className="col-md-4">
                        <h3>{cartItem.title}</h3>
                        <p className='lead fw-bold'>{cartItem.price}</p>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
    return (
        <>
            {state.length !== 0 && state.map(cartItem)}
        </>
    )
}

export default Cart
