import axios from 'axios';
import React, { useEffect, useState } from 'react'
import { NavLink } from 'react-router-dom';
// import Products_sv from '../utils/ConectorSV';
//import DATA from '../Data'
import moneyString from '../utils/Formats';

const cardItem = (item) => {
  return (
    <div className="card my-5 py-4" key={item.id} style={{ width: '18rem' }}>
      <img src={item.img} className="card-img-top" alt={item.title} />
      <div className="card-body text-center">
        <h5 className="card-title">{item.title}</h5>
        <p className="lead">{moneyString(item.price)}</p>
        <NavLink to={`/products/${item.id}`} className="btn btn-outline-primary px-3 ali">Ver</NavLink>
        <a href="#" className="btn btn-primary ms-3">Agregar al carrito</a>
      </div>
    </div>
  );
};

const Products = () => {
  //const DATA = null;
  // const DATA = await Products_sv();

  const [DATA, setData] = useState([]);
  useEffect(() => {
    const fetchData = async () => {
      const result = await axios.get('/api/products');
      console.log(result);
      setData(result.products);
    };
    fetchData();
  }, []);


  console.log(DATA);

  return (
    <div>
      <div className="container py-5">
        <div className="row">
          <div className="col-12 text-center">
            <h1>PRODUCTOS</h1>
            <hr />
          </div>
        </div>
      </div>
      <div className="container">
        <div className="row justify-content-around ">
          {DATA.map(cardItem)}
        </div>
      </div>
    </div>
  )
}

export default Products
