const addItem = localStorage.getItem('cartItems')
    ? JSON.parse(localStorage.getItem('cartItems'))
    : [];

const controlItems = (state = addItem, action) => {
    const product_in = action.payload;
    let product_before;
    console.log(product_in);

    if (product_in) {
        product_before = state.find(x => x.id === product_in.id);
    }
    let valueReturn = state;
    switch (action.type) {
        case 'ADDITEM':
            console.log(product_before);
            if(product_before){
            console.log(product_before.qty);
       console.log(product_before.qty < product_in.stock);
            }
            if (product_before == null || product_before.qty == null || product_before.qty < product_in.stock) {
                //Check if Product is Already Exist
                const exist = state.find((x) => x.id === product_in.id);
                if (exist) {
                    //Increase Quantity
                    valueReturn = state.map((x) => x.id === product_in.id ? { ...x, qty: x.qty + 1 } : x);
                } else {
                    valueReturn = [
                        ...state,
                        {
                            ...product_in,
                            qty: 1,
                        }
                    ]
                }
            } else {
                window.alert('Lo siento. Stock insuficiente...');
            }
            localStorage.setItem('cartItems', JSON.stringify(valueReturn));
            return valueReturn;

        // return [...state, action.payload]
        case 'RESTITEM':
            const exist_del = state.find((x) => x.id === product_in.id);
            if (exist_del.qty === 1) {
                valueReturn = state.filter((x) => x.id !== exist_del.id);
            } else {
                valueReturn = state.map((x) => x.id === product_in.id ? { ...x, qty: x.qty - 1 } : x);
            }
            localStorage.setItem('cartItems', JSON.stringify(valueReturn));
            return valueReturn;

        case 'DELITEM':
            valueReturn = state = state.filter((x) => {
                return x.id !== action.payload.id
            })
            localStorage.setItem('cartItems', JSON.stringify(valueReturn));
            return valueReturn;

        case 'USER_SIGNIN':
            return { ...state, userInfo: action.payload };

        default: return state;
    }
}

export default controlItems;









